sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(dateTimeString) {
		var nowDateTime = new GlideDateTime();
		var dateTime = new GlideDateTime(dateTimeString);

		return dateTime.before(nowDateTime);
	},

	isToday: function(dateTimeString) {
		var nowDate = new GlideDateTime().getLocalDate();
		var date = new GlideDateTime(dateTimeString).getLocalDate();

		return date.compareTo(nowDate) === 0;
	},

    type: 'NeedItUtils'
};
